import React, { useState, useEffect } from 'react';
import ListadoCanciones from './ListadoCanciones';
import FiltradoCanciones from './FiltradoCanciones';
import MasPopular from './MasPopular';
import cancionesInfom from '../../public/json/Spotify.json'

const SpotiMain = () => {
    const [canciones, setCanciones] = useState([]);
    
    useEffect(() => {
        const fetchCanciones = async () => {
            try {
                const response = await fetch('../../public/json/Spotify.json');
                if (!response.ok) {
                    throw new Error(`Error al cargar las canciones: ${response.status} ${response.statusText}`);
                }
                const data = await response.json();

                if (!Array.isArray(data)) {
                    throw new Error('El JSON de canciones no es un array válido');
                }

                setCanciones(data);
                setCancionesFiltradas(data);
            } catch (error) {
                console.error('Error al cargar las canciones:', error);
                setError(error.message);
                setCanciones([]);
                setCancionesFiltradas([]);
            }
        };

        fetchCanciones();
    }, []);

    useEffect(() => {
        if (filtro.trim() !== '') {
            const cancionesFiltradas = canciones.filter(
                cancion => 
                    cancion.artista && 
                    typeof cancion.artista === 'string' &&
                    cancion.artista.toLowerCase().includes(filtro.toLowerCase())
            );
            setCancionesFiltradas(cancionesFiltradas);
        } else {
            setCancionesFiltradas(canciones);
        }
    }, [filtro, canciones]);

    return (
        <div>
            <h1>SpotifEx</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <FiltradoCanciones setFiltro={setFiltro} />
            {cancionesFiltradas.length > 0 ? (
                <ListadoCanciones canciones={cancionesFiltradas} />
            ) : (
                <p>No hay canciones disponibles.</p>
            )}
            {canciones.length > 0 && <MasPopular canciones={canciones} />}
        </div>
    );
};

export default SpotiMain;
