import React, { createContext, useState } from "react";

export const contextoUsuario = createContext();

export const UsuarioProvider = ({ children }) => {
    const [usuarios, setUsuario] = useState([]);

    const validaciones = {
        usuario: /^[a-z][a-z0-9]{5,}$/,
        pass: /^(?=.*[A-Z])(?=.*\d)[A-Za-z\d]{8,}$/
    };

    const annadeUsuario = (nuevoUsuario) => {
        setUsuario([...usuarios, nuevoUsuario]);
    };

    const borraUsuario = (idUsuario) => {
        setUsuario(usuarios.filter(usuario => usuario.idUsuario !== idUsuario));
    };

    return (
        <contextoUsuario.Provider value={{ usuarios, annadeUsuario, borraUsuario, validaciones }}>
            {children}
        </contextoUsuario.Provider>
    );
};
