import React, { useContext, useState } from 'react';
import { contextoUsuario } from '../context/contextoUsuario';
import axios from 'axios';

const FormUsuario = () => {
    const { annadeUsuario, validaciones } = useContext(contextoUsuario);

    const [formulario, setFormulario] = useState({
        usuario: '',
        pass: '',
        nombre: '',
        apellidos: '',
        edad: ''
    });

    const [error, setError] = useState('');
    const [exito, setExito] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormulario(prev => ({ ...prev, [name]: value }));
    };

    const validateEdad = (fecha) => {
        const hoy = new Date();
        const nace = new Date(fecha);
        const edad = hoy.getFullYear() - nace.getFullYear();
        const mes = hoy.getMonth() - nace.getMonth();
        return edad > 18 || (edad === 18 && mes >= 0);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setExito(false);

        if (!validaciones.usuario.test(formulario.usuario)) {
            setError('El nombre de usuario no cumple con el formato requerido.');
            return;
        }

        if (!validaciones.pass.test(formulario.pass)) {
            setError('La contraseña debe tener mínimo 8 caracteres, una mayúscula y un número.');
            return;
        }

        if (!validateEdad(formulario.edad)) {
            setError('Debes tener al menos 18 años.');
            return;
        }

        try {
            const response = await axios.post('http://localhost/React/usuarioscontroller/src/api/api.php', formulario);
            annadeUsuario(response.data);
            setExito(true);
            setFormulario({
                usuario: '',
                pass: '',
                nombre: '',
                apellidos: '',
                edad: ''
            });
        } catch (err) {
            console.error(err);
            setError('Error al registrar el usuario.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h3>Registro de Usuario</h3>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {exito && <p style={{ color: 'green' }}>Usuario registrado correctamente</p>}

            <label>
                Nombre
                <input type="text" name="nombre" value={formulario.nombre} onChange={handleChange} required />
            </label>

            <label>
                Apellidos
                <input type="text" name="apellidos" value={formulario.apellidos} onChange={handleChange} required />
            </label>

            <label>
                Fecha de nacimiento
                <input type="date" name="edad" value={formulario.edad} onChange={handleChange} required />
            </label>

            <label>
                Usuario
                <input type="text" name="usuario" value={formulario.usuario} onChange={handleChange} required />
            </label>

            <label>
                Contraseña
                <input type="password" name="pass" value={formulario.pass} onChange={handleChange} required />
            </label>

            <button type="submit">Agregar Usuario</button>
        </form>
    );
};

export default FormUsuario;
