import React from "react";

const ListadoCanciones = ({ canciones }) => {
  if (!Array.isArray(canciones)) {
    console.error("❌ Error: 'canciones' no es un array:", canciones);
    return <p>Error al cargar las canciones.</p>;
  }

  return (
    <table border="1">
      <thead>
        <tr>
          <th>ID</th>
          <th>Nombre</th>
          <th>Artista</th>
          <th>Álbum</th>
          <th>Duración (s)</th>
        </tr>
      </thead>
      <tbody>
        {canciones.length > 0 ? (
          canciones.map((cancion) => (
            <tr key={cancion.track_id}>
              <td>{cancion.track_id}</td>
              <td>{cancion.track_name}</td>
              <td>{cancion.track_artist}</td>
              <td>{cancion.track_album_name}</td>
              <td>{Math.floor(cancion.track_duration_ms / 1000)}</td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan="5" style={{ textAlign: "center" }}>No hay coincidencias</td>
          </tr>
        )}
      </tbody>
    </table>
  );
};

export default ListadoCanciones;
